package day03;

public class C5_Artirma {

	public static void main(String[] args) {
		// TODO Auto-generated method stub

	int num = 15;
	num++;
	System.out.println(num);// 1 artirdi 16 oldu
	
	num= num + 7;
	System.out.println(num); // 16+7=23 oldu
	
	num+=10;
	System.out.println(num); //33 oldu
	
	num=+10; // toplama yapmadi, direkt atama yapti 10 oldu
	
	System.out.println(num);
	
	
	
	
	
	}

}
